	INTO P09_Cocktail(cocktail_nom, cocktail_prix, cocktail_verre, cocktail_categorie, cocktail_photo, cocktail_preparation, cocktail_alcoolise) VALUES ('eggnog'                      ,  1.00, 'toddy'        , 'long-drink'   , 'eggnog.jpg'                  , 'Verser dans un shaker à moitié rempli de glace le sucre, le jaune d’œuf et le cognac.Shaker et verser dans un tumbler.Compléter avec du lait, ajouter la muscade et la cannelle et ajouter un agitateur. '                                                                                                                                                   , 'T')
	INTO P09_Cocktail(cocktail_nom, cocktail_prix, cocktail_verre, cocktail_categorie, cocktail_photo, cocktail_preparation, cocktail_alcoolise) VALUES ('old fashioned'               ,  1.10, 'whisky'       , 'after-dinner' , 'OldFashioned.jpg'            , 'Écraser directement dans un verre Old Fashioned ¼ de morceau de sucre imbibé d’Angostura Bitter avec un peu de soda. Remplir le verre de glace, ajouter le Bourbon Whiskey. Remuer. Ajouter ½ tranche d’orange, la cerise à l’eau de vie et le zeste de citron.'                                                                                             , 'T')
	INTO P09_Cocktail(cocktail_nom, cocktail_prix, cocktail_verre, cocktail_categorie, cocktail_photo, cocktail_preparation, cocktail_alcoolise) VALUES ('apple jack'                  ,  2.00, 'martini'      , 'short-drink'  , 'AppleJack.jpg'               , 'Versez dans un shaker rempli de glaçons le calvados, le jus de citron et la grenadine. Secouez énergiquement et versez dans un verre à martini en filtrant les glaçons. Servez immédiatement.'                                                                                                                                                               , 'T')
	INTO P09_Cocktail(cocktail_nom, cocktail_prix, cocktail_verre, cocktail_categorie, cocktail_photo, cocktail_preparation, cocktail_alcoolise) VALUES ('jager bomb'                  ,  1.00, 'tumbler'      , 'long-drink'   , 'JagerBomb.jpg'               , 'Prenez un grand verre de type tumbler et remplissez-le de Red Bull jusqu’à environ un tiers du verre. Remplissez un verre à shot avec du Jagermeister. Plongez le verre à shot de Jagermeister dans le grand verre de Red Bull.'                                                                                                                             , 'T')
	INTO P09_Cocktail(cocktail_nom, cocktail_prix, cocktail_verre, cocktail_categorie, cocktail_photo, cocktail_preparation, cocktail_alcoolise) VALUES ('margarita'                   ,  1.10, 'margarita'    , 'short-drink'  , 'Margarita.jpeg'              , 'Humidifier avec un citron et givrer au sel fin le bord du verre sur sa moitié seulement. Verser dans shaker à demi rempli de glace le jus de citron, le Cointreau puis la Tequila. Frapper et verser dans un verre à cocktail.'                                                                                                                              , 'T')
	INTO P09_Cocktail(cocktail_nom, cocktail_prix, cocktail_verre, cocktail_categorie, cocktail_photo, cocktail_preparation, cocktail_alcoolise) VALUES ('pina colada'                 ,  2.00, 'tulipe'       , 'long-drink'   , 'pinacolada.jpg'              , 'Dans un mixer, versez les ingrédients avec 5 ou 6 glaçons et mixez le tout.'                                                                                                                                                                                                                                                                                 , 'T')
	INTO P09_Cocktail(cocktail_nom, cocktail_prix, cocktail_verre, cocktail_categorie, cocktail_photo, cocktail_preparation, cocktail_alcoolise) VALUES ('negroni'                     ,  1.10, 'whisky'       , 'after-dinner' , 'Negroni.jpg'                 , 'Verser dans le verre à mélange rempli de glace le Martini rouge, le Campari puis le Gin. Remuer et filtrer le mélange dans le verre. Ajouter la demi-tranche d’orange.'                                                                                                                                                                                      , 'T')
