	INTO P09_Cocktail(cocktail_nom, cocktail_prix, cocktail_verre, cocktail_categorie, cocktail_photo, cocktail_preparation, cocktail_alcoolise) VALUES ('blinker'                     ,  1.20, 'coupe'        , 'short-drink'  , 'Blinker.jpeg'                , 'Verser tout les ingredients et de la glace dans un shaker, verser dans le verre en filtrant dans la passoire, décorer avec les framboises'                                                                                                                                                                                                                   , 'T')
