	INTO P09_Cocktail(cocktail_nom, cocktail_prix, cocktail_verre, cocktail_categorie, cocktail_photo, cocktail_preparation, cocktail_alcoolise) VALUES ('blue hawaiian'               ,  1.00, 'tulipe'       , 'long-drink'   , 'blue-hawaii.jpg'             , 'Remplir le shaker de glace, ajouter le rhum, le curaçao, le sirop, le lait de coco et le jus d''ananas. Mélanger et verser le mélange dans le verre en flitrant avec la passoire. Décorer avec un quart de tranche d''ananas.'                                                                                                                               , 'T')
