INSERT INTO P09_Cocktail(cocktail_nom, cocktail_prix, cocktail_verre, cocktail_categorie, cocktail_photo, cocktail_preparation, cocktail_alcoolise) VALUES
	('bloody mary'                 ,  1.10, 'tumbler'      , 'long-drink'   , 'BloodyMary.jpg'              , 'Agiter les ingrédients dans un verre à mélange avec des glaçons (pour refroidir sans trop diluer). Verser dans le verre, puis ajouter à convenance sel de céleri, sel et poivre. Servir.'                                                                                                                                                                    ,  true),
	('diabolo grenadine'           ,  0.80, 'tumbler'      , 'long-drink'   , 'DiaboloGrenadine.jpg'        , 'Versez la grenadine dans le verre et complétez avec de la limonade bien fraîche.'                                                                                                                                                                                                                                                                            , false),
	('diabolo menthe'              ,  0.80, 'tumbler'      , 'long-drink'   , 'DiaboloMenthe.jpg'           , 'Versez la menthe dans le verre et complétez avec de la limonade bien fraîche.'                                                                                                                                                                                                                                                                               , false),
	('americano'                   ,  1.10, 'tumbler'      , 'long-drink'   , 'Americano.jpg'               , 'Directement dans un tumbler rempli de glace, verser le Martini rouge, le Campari, compléter de Perrier. Décorer couper puis décorer avec d’une demi-tranche de citron et d’orange. Ajouter un agitateur.'                                                                                                                                                    ,  true),
	('eggnog'                      ,  1.00, 'toddy'        , 'long-drink'   , 'eggnog.jpg'                  , 'Verser dans un shaker à moitié rempli de glace le sucre, le jaune d’œuf et le cognac.Shaker et verser dans un tumbler.Compléter avec du lait, ajouter la muscade et la cannelle et ajouter un agitateur. '                                                                                                                                                   ,  true),
	('old fashioned'               ,  1.10, 'whisky'       , 'after-dinner' , 'OldFashioned.jpg'            , 'Écraser directement dans un verre Old Fashioned ¼ de morceau de sucre imbibé d’Angostura Bitter avec un peu de soda. Remplir le verre de glace, ajouter le Bourbon Whiskey. Remuer. Ajouter ½ tranche d’orange, la cerise à l’eau de vie et le zeste de citron.'                                                                                             ,  true),
	('apple jack'                  ,  2.00, 'martini'      , 'short-drink'  , 'AppleJack.jpg'               , 'Versez dans un shaker rempli de glaçons le calvados, le jus de citron et la grenadine. Secouez énergiquement et versez dans un verre à martini en filtrant les glaçons. Servez immédiatement.'                                                                                                                                                               ,  true),
	('jager bomb'                  ,  1.00, 'tumbler'      , 'long-drink'   , 'JagerBomb.jpg'               , 'Prenez un grand verre de type tumbler et remplissez-le de Red Bull jusqu’à environ un tiers du verre. Remplissez un verre à shot avec du Jagermeister. Plongez le verre à shot de Jagermeister dans le grand verre de Red Bull.'                                                                                                                             ,  true),
	('margarita'                   ,  1.10, 'margarita'    , 'short-drink'  , 'Margarita.jpeg'              , 'Humidifier avec un citron et givrer au sel fin le bord du verre sur sa moitié seulement. Verser dans shaker à demi rempli de glace le jus de citron, le Cointreau puis la Tequila. Frapper et verser dans un verre à cocktail.'                                                                                                                              ,  true),
	('pina colada'                 ,  2.00, 'tulipe'       , 'long-drink'   , 'pinacolada.jpg'              , 'Dans un mixer, versez les ingrédients avec 5 ou 6 glaçons et mixez le tout.'                                                                                                                                                                                                                                                                                 ,  true),
	('negroni'                     ,  1.10, 'whisky'       , 'after-dinner' , 'Negroni.jpg'                 , 'Verser dans le verre à mélange rempli de glace le Martini rouge, le Campari puis le Gin. Remuer et filtrer le mélange dans le verre. Ajouter la demi-tranche d’orange.'                                                                                                                                                                                      ,  true),
	('alpine negroni'              ,  1.30, 'whisky'       , 'after-dinner' , 'AlpineNegroni.jpg'           , 'Verser les ingredients dans un verre à mélange, ajouter de la glace et remuer. verser en filtrant dans le verre et ajouter la branche de romarin'                                                                                                                                                                                                            ,  true),
	('blinker'                     ,  1.20, 'coupe'        , 'short-drink'  , 'Blinker.jpeg'                , 'Verser tout les ingredients et de la glace dans un shaker, verser dans le verre en filtrant dans la passoire, décorer avec les framboises'                                                                                                                                                                                                                   ,  true),
	('pacha monte-carlos'          ,  2.00, 'tulipe'       , 'long-drink'   , 'montecarlo.jpg'              , 'Placer les pèches avec les autres ingredients dans un mixeur, ajouter la glace pilée et mixer, verser dans le verre et décorer avec 2 cerises.'                                                                                                                                                                                                              ,  true),
	('mint julep'                  ,  1.20, 'whisky'       , 'long-drink'   , 'Mintjulep.jpeg'              , 'Directement dans le verre old fashioned, piler les feuilles de menthe et le sucre.Remplir de glace pilée.Verser le Bourbon Whiskey et décorer avec une cerise à l’eau de vie et feuilles de menthe fraîche. Ajouter une paille.'                                                                                                                             ,  true),
	('blue hawaiian'               ,  1.00, 'tulipe'       , 'long-drink'   , 'blue-hawaii.jpg'             , 'Remplir le shaker de glace, ajouter le rhum, le curaçao, le sirop, le lait de coco et le jus d''ananas. Mélanger et verser le mélange dans le verre en flitrant avec la passoire. Décorer avec un quart de tranche d''ananas.'                                                                                                                               ,  true),
	('miami beach'                 ,  1.20, 'martini'      , 'short-drink'  , 'Miami_Beach.jpg'             , 'Mettez les glaçons, le gin, le jus d''ananas et le sirop de sucre dans le shaker. Secouez avec énergie pendant 10 secondes. Filtrez et versez dans un verre martini.'                                                                                                                                                                                        ,  true),
	('havana beach'                ,  1.30, 'martini'      , 'short-drink'  , 'havana-beach.jpg'            , 'Mettre des glaçons dans le shaker. Ajouter le rhum blanc, le jus d''ananas, le jus de citron vert et le sirop de sucre de canne. Frapper et verser dans un verre à cocktail.'                                                                                                                                                                                ,  true),
	('swimming pool'               ,  2.00, 'tulipe'       , 'long-drink'   , 'Swimming pool.jpeg'          , 'Verser les ingrédients dans un shaker et mélanger 15 secondes.verser dans le verre et décorer avec une tranche d''ananas et une cerise.'                                                                                                                                                                                                                     ,  true),
	('rusty nail'                  ,  1.10, 'whisky'       , 'after-dinner' , 'Rusty Nail.jpg'              , 'Remplir un verre de glace. Insérer le whisky puis la liqueur. Remuer à l''aide d''une cuillère à mélange. Insérer un long zeste de citron.'                                                                                                                                                                                                                  ,  true),
	('vin chaud'                   ,  1.00, 'chope'        , 'after-dinner' , 'vin cho.jpg'                 , 'Faire chauffer le tout à feu moyen et constant pendant 20 minutes minimum, pour dissoudre le sucre et imprégner le vin de l''odeur de cannelle. Verser dans le verre en filtrant les clous de girofle et consommer...'                                                                                                                                       ,  true),
	('hot buttered rum'            ,  2.00, 'toddy'        , 'after-dinner' , 'rum.jpg'                     , 'Verser directement dans le verre la cassonade et l''eau chaude. Remuer à l''aide d''une cuillère à mélange. Ajouter le rhum et le morceau de beurre. Ajouter selon votre goût de la cannelle, noix de muscade, gingembre et/ou gousse de vanille.'                                                                                                           ,  true),
	('irish coffee'                ,  2.15, 'toddy'        , 'after-dinner' , 'irishcoffee.jpg'             , 'Mettre de l''eau chaude dans le verre pendant 5 minutes pour le préchauffer puis retirer l''eau. Chauffer le whisky avec le sucre sans le faire porter à ébullition. Verser le mélange dans le verre puis ajouter le café chaud. Fouetter la crème et venir la déposer sur le dessus du verre de type "chope". '                                             ,  true),
	('espresso martini'            ,  1.20, 'martini'      , 'after-dinner' , 'martinicafé.jpg'             , 'Frapper au shaker avec quelques glaçons la vodka, la liqueur de café kahlua, le café et le sirop de sucre.Verser dans un verre à martini en filtrant la glace.'                                                                                                                                                                                              ,  true),
	('amaro sour'                  ,  1.70, 'coupe'        , 'short-drink'  , 'amaro.jpeg'                  , 'Ajouter tout les ingrédients sauf l''oeuf dans le shaker rempli de glace et mélanger 15 secondes. Filtrer dans leshaker, ajouter le blanc de l''oeuf et remélanger 15 secondes. Verser dans la coupe et décorer avec la cerise. '                                                                                                                            ,  true),
	('amaretto sour'               ,  1.10, 'whisky'       , 'short-drink'  , 'amaretto.jpeg'               , 'Placez quelques glaçons dans le verre. Frappez les ingrédients au shaker avec des glaçons puis versez dans le verre en retenant la glace du shaker.'                                                                                                                                                                                                         ,  true),
	('beer float'                  ,  1.00, 'pilsner'      , 'long-drink'   , 'beer float.jpg'              , 'Versez la boisson dans le verre et ajoutez la boule de glace au dessus'                                                                                                                                                                                                                                                                                      ,  true),
	('soda float'                  ,  1.00, 'pilsner'      , 'long-drink'   , 'lemonFloat.jpg'              , 'Versez la boisson dans le verre et ajoutez la boule de glace au dessus'                                                                                                                                                                                                                                                                                      , false),
	('rio'                         ,  0.80, 'whisky'       , 'short-drink'  , 'RIO.jpg'                     , 'versez votre jus d''orange, ajoutez la grenadine puis la limonade. Ajoutez une ou deux rondelles de citron vert en décoration sur le bord. Remuer légèrement avec la paille et servez'                                                                                                                                                                       , false),
	('fuzzy asshole'               ,  1.00, 'toddy'        , 'after-dinner' , 'fuzzy.jpg'                   , 'Remplir à moitié de café chaud et a moitié de schnapps, remuer.'                                                                                                                                                                                                                                                                                             ,  true),
	('pink margarita'              ,  1.10, 'tumbler'      , 'short-drink'  , 'margaritarose.jpg'           , 'Verser les jus et le sirop, mélanger à la cuillère, complèter de limonade.'                                                                                                                                                                                                                                                                                  , false),
	('august moon'                 ,  1.30, 'tumbler'      , 'long-drink'   , 'august-moon.jpg'             , 'Frappez les ingrédients au shaker, puis passez dans un verre rafraîchi.'                                                                                                                                                                                                                                                                                     , false),
	('atomic freeze'               ,  3.00, 'ballon'       , 'long-drink'   , 'Atomic.jpg'                  , 'Laver et couper les fraises en morceaux. Ajouter dans le mixeur avec le restant des ingrédients et des glaçons. Mixer durant quelques secondes et verser dans de grands verres à vin. '                                                                                                                                                                      , false),
	('sweet dreams'                ,  2.00, 'coupe'        , 'short-drink'  , 'sweetdreams.jpeg'            , 'Récupérer seulement le blanc de l''oeuf. Verser tous les ingrédients dans un shaker, sauf le champagne. Remuer vigoureusement sans glaçons pour monter le blanc d’œuf en neige. Ajouter des glaçons dans le shaker puis remuer. Verser le contenu dans le verre en le filtrant à l’aide d‘une passoire. Allonger le mocktail avec le champagne.'             , false),
	('apple cobbler'               ,  1.10, 'whisky'       , 'short-drink'  , 'apple cobbler.jpg'           , 'Verser les ingrédient sur des glaçons. Bien mélanger et servir frais. Décorer avec la tranche de pomme'                                                                                                                                                                                                                                                      , false),
	('apple fizz'                  ,  1.20, 'tumbler'      , 'long-drink'   , 'AppleFizz.jpeg'              , 'Mettre tout les ingredients dans le shaker avec de la glace. frappper et verser dans le verre.'                                                                                                                                                                                                                                                              , false),
	('apricot beach'               ,  1.50, 'tumbler'      , 'long-drink'   , 'Abricot.jpeg'                , 'Récupérer seulement le jaune de l''oeuf. Mixer tout les ingrédients avec de la glace pilée jusqu''à obtenir une mousse onctueuse et verser dans le verre'                                                                                                                                                                                                    , false),
	('banana sprint'               ,  2.00, 'tumbler'      , 'long-drink'   , 'banana.jpeg'                 , 'Mixer tout les ingrédients avec de la glace pilée jusqu''à obtenir une mousse onctueuse et verser dans le verre'                                                                                                                                                                                                                                             , false),
	('bloody orange'               ,  1.10, 'tumbler'      , 'long-drink'   , 'bloody-orange.jpg'           , 'Mélanger tout les ingredients au shaker avec un peu de glace pilée et verser.'                                                                                                                                                                                                                                                                               , false),
	('honeymoon'                   ,  1.20, 'tumbler'      , 'long-drink'   , 'honeymoon.jpeg'              , 'Frapper et passer dans le verre.'                                                                                                                                                                                                                                                                                                                            , false),
	('alice'                       ,  1.30, 'tumbler'      , 'long-drink'   , 'Alice.jpg'                   , 'Frapper au shaker avec quelques glaçons la grenadine, le jus d''orange, le jus d''ananas et la crème liquide.Verser dans un verre highball.'                                                                                                                                                                                                                 , false),
	('20e siecle'                  ,  1.00, 'martini'      , 'short-drink'  , '20esiecle.jpg'               , 'À l''aide d''un gobelet doseur, mesurer la quantité exacte d''ingrédients, puis les verser dans un shaker et agiter vigoureusement. verser dans le verre.'                                                                                                                                                                                                   ,  true),
	('pussy cat'                   ,  1.20, 'whisky'       , 'short-drink'  , 'pussycat.jpg'                , 'Mettre tout les ingredients dans le shaker avec de la glace. frappper et filtrer dans le verre.'                                                                                                                                                                                                                                                             ,  true),
	('buccaneer'                   ,  2.00, 'pilsner'      , 'long-drink'   , 'buc.jpeg'                    , 'Verser la biere dans le verre et ajouter le shooter de bacardi en remuant doucement'                                                                                                                                                                                                                                                                         ,  true),
	('mai tai'                     ,  1.10, 'whisky'       , 'short-drink'  , 'classic-mai-tai.jpeg'        , 'Associez tous les ingrédients avec de la glace dans un shaker. Frappez et filtrez dans un verre à whisky.'                                                                                                                                                                                                                                                   ,  true),
	('moscow mule'                 ,  2.00, 'chope'        , 'long-drink'   , 'mule.jpg'                    , 'Mettre la Glace dans un grand verre. Verser les ingrédients et remuer délicatement avec la cuillère à cocktail. Servir.'                                                                                                                                                                                                                                     ,  true),
	('aperol spritz'               ,  1.40, 'ballon'       , 'long-drink'   , 'aperol.jpg'                  , 'Mettre 1 tranche d''orange et de la glace. Mettre l''Aperol.Remplir ensuite avec le Prosecco. Ajoutez un trait d''eau gazeuse et mélangez avec une cuillère. '                                                                                                                                                                                               ,  true),
	('bamboo'                      ,  1.20, 'coupe'        , 'long-drink'   , 'bamboo.jpg'                  , 'Verser le vermouth et le sherry dans le verre à mélange avec quelques glaçons. Remuer soigneusement et ajouter l''angostura. Servir dans un verre à cocktail et décorer d''une olive.'                                                                                                                                                                       ,  true),
	('pisco sour'                  ,  1.40, 'whisky'       , 'short-drink'  , 'pisco.jpg'                   , 'Ajouter le pisco, le jus de citron vert, le sirop de sucre et le blanc d''œuf dans un shaker et agiter pendant environ 15 secondes. Ajouter une poignée de glaçons et secouer à nouveau pendant 15 secondes. Filtrer le contenu dans un verre pré-refroidi. Verser l''Angostura Bitters sur le dessus de la mousse.'                                         ,  true),
	('caipirinha'                  ,  1.12, 'whisky'       , 'short-drink'  , 'caipi.jpeg'                  , 'Lavez le citron puis coupez-le en deux dans le sens de la hauteur. Enlevez la partie blanche centrale, source d’amertume. Découpez le citron en petits dés et placez-les dans le verre. Ajoutez le sirop de sucre. Ecraser dans le verre a l''aide du pilon. Ajouter de la glace et la cachaca. Mélanger et servez.'                                         ,  true);
